var BODY=document.getElementsByTagName("BODY")[0];function closeNav(){document.getElementsByClassName("nav-list")[0].classList.remove("active"),document.getElementsByClassName("blur-overlay")[0].style.display="none",BODY.classList.remove("lock")}AOS.init({duration:1200}),document.getElementsByClassName("nav-link")[1].addEventListener("click",function(){closeNav()}),document.getElementsByClassName("open")[0].addEventListener("click",function(){document.getElementsByClassName("nav-list")[0].classList.add("active"),document.getElementsByClassName("blur-overlay")[0].style.display="initial",BODY.classList.add("lock")}),document.getElementsByClassName("close")[0].addEventListener("click",function(){closeNav()});